import React, {useState} from 'react'

const Signup = () => {

  const [passwordType, setPasswordType] = useState("password");
    const [passwordInput, setPasswordInput] = useState("");
    const handlePasswordChange =(evnt)=>{
        setPasswordInput(evnt.target.value);
    }

  const togglePassword =()=>{
    if(passwordType==="password")
    {
     setPasswordType("text")
     return;
    }
    setPasswordType("password")
  }



  return (
    <div className='leftSideInnerArea'>
      <h1>Let's set up your Account</h1>
      <p>Already have an account? <a href="/#" onClick={(e) => e.preventDefault()}>Sign in</a></p>

      <div className='formOuterBlock'>
        <div className="formGroup field">
          <input type="input" className="formField" placeholder="Name" name="n-name" onChange={e => e.target.value} />
          <label htmlFor="n-name" className="formLabel">Your Name</label>
        </div>
        <div className="formGroup field">
          <input type="input" className="formField" placeholder="Email Address" name="e-email" onChange={e => e.target.value} />
          <label htmlFor="email" className="formLabel">Email Address</label>
        </div>
        <div className="formGroup field">
          <select className='selectedValues' onChange={e => e.target.value}>
            <option value="A">I would describe my user type as</option>
            <option value="A">Field Value 1</option>
            <option value="B">Field Value 2</option>
            <option value="C">Field Value 3</option>
          </select>
        </div>
        <div className="formGroup field">
          <input type="password" className="formField" placeholder="Password" name="passwd" onChange={e => e.target.value} />
          <label htmlFor="passwd" className="formLabel">Password</label>
          <div className="input-group-btn">
            <button className="btn btn-outline-primary" onClick={togglePassword}>
            { passwordType==="password"? <i className="bi bi-eye-slash"></i> :<i className="bi bi-eye"></i> }
            </button>
          </div>


          <p className='passwdInformation'>Minimum 8 characters</p>
        </div>
        <button type='submit' className='submitBtn'>Next</button>
      </div>

      <div className='instructionBlock'>
        <p>By clicking the 'Next' button you agree to creating a free account and to <a href='/#' onClick={(e) => e.preventDefault()}>Term and Service</a> and <a href='/#' onClick={(e) => e.preventDefault()}>Privacy and Policy</a></p>
      </div>
    </div>
  )
}

export default Signup;
